import {
  useState,
  useRef
} from "react";

import classes from "./AuthForm.module.css";

const AuthForm = () => {
  const emailInputRef = useRef ();
  const passwordInputRef = useRef ();
  const [ isLoading, setIsLoading ] = useState ( false );
  const [ isLogin, setIsLogin ] = useState ( true );

  const handleSwitchAuthMode = () => {
    setIsLogin (
      ( prevState ) => !prevState
    );
  };

  const handleSubmit = ( event ) => {
    event.preventDefault ();

    const enteredEmail = emailInputRef.current.value;
    const enteredPassword = passwordInputRef.current.value;
    const context = 

    // *optional -> adding validation

    setIsLoading ( true );
    let url;

    if ( isLogin ) {
      url = "https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyBZ9wc5jvpLUYnZefBqWCWlsocKQjUsqCQ";
    } else {
      url = "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyBZ9wc5jvpLUYnZefBqWCWlsocKQjUsqCQ";
    };

    fetch (
      url,
      {
        method: "POST",
        body: JSON.stringify ({
          email: enteredEmail,
          password: enteredPassword,
          returnSecureToken: true
        }),
        headers: {
          "Content-Type": "application/json"
        }
      }
    ).then(
      ( res ) => {
        setIsLoading ( false );
        if ( res.ok ) {
          return res.json();
        } else {
          return res.json().then(
            ( data ) => {
              let errorMessage = "Authentication message failed!";
              // if ( data && data.error && data.error.message ) {
              //   errorMessage = data.error.message;
              // };

              throw new Error ( errorMessage );
            }
          ).then(
            data => {
              console.log( data );
            }
          ).catch(
            err => {
              alert ( err.message );
            }
          );
        };
      }
    );

  };

  return (
    <section className = { classes.auth }>

      <h1>
        {
          isLogin ? "Login" : "Sign Up"
        }
      </h1>

      <form onSubmit = { handleSubmit }>

        <div className = { classes.control }>

          <label htmlFor = "email">
            Your Email
          </label>

          <input
            type = "email"
            id = "email"
            ref = { emailInputRef }
            required
          />

        </div>

        <div className = { classes.control }>

          <label htmlFor = "password">
            Your Password
          </label>

          <input
            type = "password"
            id = "password"
            ref = { passwordInputRef }
            required
          />

        </div>

        <div className = { classes.actions }>

        { !isLoading && 
          (
          <button>
            {
              isLogin ? "Login" : "Create Account"
            }
          </button>
          )
        }

        {
          isLoading && <p>Still Loading Request ...</p>
        }
          <button
            type = "button"
            className = { classes.toggle }
            onClick = { handleSwitchAuthMode }
          >
            {
              isLogin ? "Create new account" : "Login with existing account"
            }
          </button>

        </div>

      </form>

    </section>
  );
};

export default AuthForm;
